mapper.select:
select
channel_id
from tb_channel_config
where
status != -1

  AND merchant_id = #{merchantId}


  AND company_id = #{companyId}


  AND company_id IN
   #{companyId}


  AND shop_id = #{shopId}

select
channelConfig.conf_id, channelConfig.channel_id, channelConfig.merchant_id, channelConfig.shop_id,
channelConfig.company_id, channelConfig.appkey, channelConfig.secret,channelConfig.create_time,
channelConfig.update_time, channelConfig.status, `channel`.channel_name, channelConfig.channel_source_id
from tb_channel_config `channelConfig`
LEFT JOIN tb_channel `channel`ON channelConfig.channel_id = channel.channel_id
where
channelConfig.status != -1

  AND channelConfig.company_id IN
   #{companyId}


  AND channelConfig.merchant_id = #{merchantId}


  AND channelConfig.merchant_id IN
   #{merchantId}


  AND channelConfig.company_id = #{companyId}


  AND channelConfig.shop_id = #{shopId}

ORDER BY channelConfig.create_time DESC

mapper.update:
update tb_channel_config


    update_time = #{updateTime,jdbcType=TIMESTAMP},


    status = #{status,jdbcType=INTEGER}


where
conf_id = #{confId}
