mapper.select:
SELECT

FROM tb_order_distribution_status
WHERE order_id = #{orderId,jdbcType=CHAR}
AND
channel_id = #{channelId,jdbcType=INTEGER}
AND
status_code = #{statusCode}
limit 0,1

SELECT

FROM tb_order_distribution_status
WHERE order_id = #{orderId,jdbcType=CHAR}
AND
channel_id = #{channelId,jdbcType=INTEGER}
ORDER BY create_time ASC
