mapper.select:
SELECT
sum(order_num) as order_num, DATE_FORMAT(create_time,'%Y-%m-%d %H') as create_time
FROM stat_order
WHERE
1=1

  AND channel_id = #{channelId,jdbcType=INTEGER}


  AND merchant_id = #{merchantId,jdbcType=INTEGER}


  AND company_id = #{companyId,jdbcType=INTEGER}


  company_id IN
   #{companyId}


  AND shop_id = #{shopId,jdbcType=INTEGER}


  AND city_code = #{cityCode,jdbcType=CHAR}


  AND create_time >= #{startTime}


  AND create_time <= #{endTime}

GROUP BY DATE_FORMAT(create_time,'%Y-%m-%d %H')

